package Arrays;

public class Matrix {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		
		
		int arr1[][] = {{2,7,9},{3,6,1},{7,4,2}};
		
		//Printing 2D array
		
		for(int i=0; i<3; i++)
		{
			for(int j=0; j<3; j++)
						
				System.out.print(arr1[i][j] + " ");
				
				System.out.println();
			
		}
	}

}
